                    if (fromNumber != null && targetPhoneNumber != null && 
                        fromNumber.contains(targetPhoneNumber!!)) {

                        // 触发回调
                        callback?.onSmsReceived(fromNumber, messageBody.toString())
                    }
                }
            }
        }
    }
}

