class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContent {
            TiaozilaileTheme {
                Scaffold(modifier = Modifier.fillMaxSize()) { innerPadding ->
                    MainScreen(
                        modifier = Modifier.padding(innerPadding)
                    )
                }
            }
        }
    }
}

@Composable
fun MainScreen(modifier: Modifier = Modifier) {
    var phoneNumber by remember { mutableStateOf("") }
    var isEditing by remember { mutableStateOf(false) }
    var isListening by remember { mutableStateOf(false) }
    var isTestingForeground by remember { mutableStateOf(false) }
    val rippleAnimation = remember { Animatable(0f) }
    val context = LocalContext.current

    // 从SharedPreferences加载保存的电话号码
    val sharedPreferences = remember {
        context.getSharedPreferences("app_prefs", Context.MODE_PRIVATE)
    }

    // 初始化时加载保存的电话号码
    val savedPhoneNumber = remember {
        sharedPreferences.getString("phone_number", "") ?: ""
    }

    if (phoneNumber.isEmpty() && savedPhoneNumber.isNotEmpty()) {
        phoneNumber = savedPhoneNumber
    }

    // 设置短信监听回调
    LaunchedEffect(isListening) {
        if (isListening) {
            // 设置短信接收回调
            SmsListenerManager.setCallback(object : SmsListenerService.SmsListenerCallback {
                override fun onSmsReceived(fromNumber: String, message: String) {
                    // TODO: 处理接收到的短信
                    // 这里可以添加通知、震动等提醒功能
                    println("收到来自 $fromNumber 的短信: $message")
                }
            })
        } else {
            SmsListenerManager.removeCallback()
        }
    }

    // 波纹动画效果
    LaunchedEffect(isListening) {
        if (isListening) {
            rippleAnimation.animateTo(
                targetValue = 1f,
                animationSpec = infiniteRepeatable(
                    animation = tween(durationMillis = 1500),
                    repeatMode = RepeatMode.Restart
                )
            )
        } else {
            rippleAnimation.snapTo(0f)
        }
    }

    /**
     * 启动短信监听服务
     */
    fun startSmsListening() {
        if (phoneNumber.isNotEmpty()) {
            isListening = true
            SmsListenerManager.startListening(context, phoneNumber)
        }
    }

    /**
     * 停止短信监听服务
     */
    fun stopSmsListening() {
        isListening = false
        SmsListenerManager.stopListening(context)
    }

    /**
     * 测试前台服务效果
     */
    fun testForegroundService() {
        if (isTestingForeground) {
            // 停止测试服务
            isTestingForeground = false
            SmsListenerManager.stopListening(context)
        } else {
            // 启动测试服务（不监听短信，只展示前台服务效果）
            isTestingForeground = true
            val intent = Intent(context, SmsListenerService::class.java).apply {
                putExtra("target_phone_number", "test-mode")
            }

            if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                context.startForegroundService(intent)
            } else {
                context.startService(intent)
            }
        }
    }

    Box(
        modifier = modifier.fillMaxSize(),
        contentAlignment = Alignment.Center
    ) {
        Column(
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.Center,
            modifier = Modifier.padding(16.dp)
        ) {
            // 电话号码输入框
            OutlinedTextField(
                value = phoneNumber,
                onValueChange = { if (isEditing) phoneNumber = it },
                label = { Text("电话号码") },
                placeholder = { Text("请输入电话号码") },
                keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Phone),
                enabled = isEditing,
                modifier = Modifier.padding(bottom = 48.dp)
            )

            // 编辑/保存按钮
            Button(
                onClick = {
                    if (isEditing) {
                        // 保存电话号码到SharedPreferences
                        sharedPreferences.edit()
                            .putString("phone_number", phoneNumber)
                            .apply()
                    }
                    isEditing = !isEditing
                },
                modifier = Modifier.padding(bottom = 32.dp)
            ) {
                Text(if (isEditing) "保存" else "编辑")
            }

            // 大的圆形按钮与波纹效果
            Box(
                modifier = Modifier.size(160.dp),
                contentAlignment = Alignment.Center
            ) {
                // 波纹效果
                if (isListening) {
                    Canvas(
                        modifier = Modifier.size(160.dp)
                    ) {
                        val radius = 80.dp.toPx() * rippleAnimation.value
                        drawCircle(
                            color = Color.Red.copy(alpha = 0.3f),
                            radius = radius,
                            style = Stroke(width = 4f)
                        )
                    }
                }

                // 大的圆形按钮
                Button(
                    onClick = {
                        if (isListening) {
                            stopSmsListening()
                        } else {
                            startSmsListening()
                        }
                    },
                    enabled = phoneNumber.isNotEmpty() && !isEditing,
                    modifier = Modifier.size(140.dp),
                    colors = ButtonDefaults.buttonColors(
                        containerColor = if (isListening) Color.Red else ButtonDefaults.buttonColors().containerColor
                    )
                ) {
                    Text(
                        text = if (isListening) "监听中" else "启动",
                        fontSize = 20.sp
                    )
                }
            }

            // 测试前台服务按钮
            Row(
                modifier = Modifier.padding(top = 64.dp),
                horizontalArrangement = Arrangement.spacedBy(16.dp)
            ) {
                Button(
                    onClick = { testForegroundService() },
                    colors = ButtonDefaults.buttonColors(
                        containerColor = if (isTestingForeground) Color.Green else Color.Blue
                    )
                ) {
                    Text(if (isTestingForeground) "停止测试" else "测试前台服务")
                }

                if (isTestingForeground) {
