@Composable
fun MonitorSourceRoute() {
 val context = LocalContext.current
 val coroutineScope = rememberCoroutineScope()
 val storedValue by context.monitorDataStore.data
 .map { preferences -> preferences[MONITOR_SOURCE_KEY] ?: DEFAULT_MONITOR_SOURCE }
 .collectAsState(initial = DEFAULT_MONITOR_SOURCE)

 var isEditing by rememberSaveable { mutableStateOf(false) }
 var inputValue by rememberSaveable { mutableStateOf(DEFAULT_MONITOR_SOURCE) }

 LaunchedEffect(storedValue, isEditing) {
 if (!isEditing) {
 inputValue = storedValue
 }
 }

 MonitorSourceContent(
 value = inputValue,
 storedValue = storedValue,
 isEditing = isEditing,
 onValueChange = { inputValue = it },
 onEditClick = { isEditing = true },
 onSaveClick = {
 coroutineScope.launch {
 context.monitorDataStore.edit { preferences ->
 preferences[MONITOR_SOURCE_KEY] = inputValue.ifBlank { DEFAULT_MONITOR_SOURCE }
 }
 isEditing = false
 }
 }
 )
}

@Composable
fun MonitorSourceContent(
 value: String,
 storedValue: String,
 isEditing: Boolean,
 onValueChange: (String) -> Unit,
 onEditClick: () -> Unit,
 onSaveClick: () -> Unit
) {
 Surface(
 modifier = Modifier.fillMaxSize(),
 color = MaterialTheme.colorScheme.background
 ) {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(24.dp),
 contentAlignment = Alignment.Center
 ) {
 Column(
 modifier = Modifier.fillMaxWidth(0.85f),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 Text(
 text = "监控短信来源",
 style = MaterialTheme.typography.titleLarge
 )
 OutlinedTextField(
 value = value,
 onValueChange = onValueChange,
 enabled = isEditing,
 label = { Text(text = "请输入内容") },
 singleLine = true,
 modifier = Modifier.fillMaxWidth(),
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number)
 )
 Row(
 horizontalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 Button(
 onClick = onEditClick,
 enabled = !isEditing
 ) {
 Text(text = "编辑")
 }
 Button(
 onClick = onSaveClick,
 enabled = isEditing
 ) {
 Text(text = "保存")
 }
 }
 Text(
 text = "当前保存内容：$storedValue",
 style = MaterialTheme.typography.bodyMedium
 )
 }
 }
 }
}

